modules.usage.skir:
// Example usage of Skir imports and definitions

import * as geometry from "./geometry.skir";

// Define a new shape using imported types
struct Rectangle(957314) {
  top_left: geometry.Point;
  bottom_right: geometry.Point;
  color: geometry.Color;
}

const SAMPLE_RECTANGLE: Rectangle = {
  top_left: {
    x: 0,
    y: 0,
    label: "top-left",
  },
  bottom_right: {
    x: 100,
    y: 100,
    label: "bottom-right",
  },
  color: {
    r: 255,
    g: 255,
    b: 0,
    label: "yellow",
  },
};

// Example of using nested types with enums
enum ShapeType {
  POINT;
  CIRCLE;
  RECTANGLE;
}

struct Shape {
  type: ShapeType;
  label: string;

  // Example of a nested struct
  struct Dimensions {
    width: float32;
    height: float32;
  }

  dimensions: Dimensions?;
}

// Example of a method definition
method CalculateArea(Shape): float64 = 201181;

// Example of removed fields
struct VersionedData {
  version: int32;
  data: string;
  removed;  // Field was removed
  metadata: string;
}

modules.demo.skir:
// Example of Skir features

// Struct definition with explicit field numbers
struct User(100) {
  user_id: int32 = 0;
  username: string = 1;
  email: string = 2;
  active: bool = 3;
}

// Enum definition
enum UserRole {
  VIEWER;
  EDITOR;
  ADMIN;
}

// Enum with value fields
enum ApiResponse {
  SUCCESS;
  error: string;
  rate_limited: int32;  // Seconds to wait before retrying
}

// Example of keyed arrays
struct UserRegistry {
  users: [User|user_id];
}

// Example of optional types
struct UserSettings {
  theme: string?;
  notifications_enabled: bool;
  preferred_language: string?;
}

// Const definitions
const SYSTEM_USER: User = {
  user_id: 0,
  username: "system",
  email: "system@example.com",
  active: true,
};

const DEFAULT_ROLE: UserRole = "VIEWER";

const SERVER_ERROR: ApiResponse = {
  kind: "error",
  value: "Internal Server Error",
};

// Method definitions for RPC interface
method GetUser(int32): User = 500;
method UpdateUser(User): ApiResponse = 501;

// Example of timestamp type
struct AuditLog {
  timestamp: timestamp;

  user_id: int32;
  action: string;
}
